# detects missing gaps in the detected content
# gaps in spaces don't matter to voice to text
# so are ignored and reduced to a single character
def gap_analysis(detected_content_columns):
    # detect large gaps and add breaks
    detected_boundary_gaps = []
    detected_boundary_columns = []

    # add first value
    detected_boundary_gaps.append(detected_content_columns[0])
    for index in range(0, len(detected_content_columns) - 1):
        if (detected_content_columns[index] + 1) != detected_content_columns[index + 1]:
            # keep both columns at boundary
            detected_boundary_gaps.append(detected_content_columns[index])
            detected_boundary_gaps.append(detected_content_columns[index + 1])
    # add last value
    detected_boundary_gaps.append(detected_content_columns[-1])
    # print(detected_boundary_gaps)

    for index in range(0, len(detected_boundary_gaps) - 1):
        gap_differnece = detected_boundary_gaps[index + 1] - detected_boundary_gaps[index]
        if gap_differnece >= 25 and gap_differnece <= 44:  # 16 + 8 pixels or 1.5 characters  32 + 8 or 2.5 characters
            print("gap: ", detected_boundary_gaps[index], detected_boundary_gaps[index + 1], "  gap difference: ", gap_differnece)
            if gap_differnece % 2 != 0:
                # odd so add two columns
                value1 = detected_boundary_gaps[index] + gap_differnece // 2
                value2 = detected_boundary_gaps[index] + gap_differnece // 2 + 1
                if value1 in detected_content_columns:
                    # then this is too long a letter and remove the value
                    print("removing: ", value1)
                    detected_content_columns.remove(value1)

                if value2 in detected_content_columns:
                    # then this is too long a letter and remove the value
                    print("removing: ", value2)
                    detected_content_columns.remove(value2)

            else:
                # even so add one column
                value = detected_boundary_gaps[index] + gap_differnece // 2
                if value in detected_content_columns:
                    # then this is too long a letter and remove the value
                    print("removing: ", value)
                    detected_content_columns.remove(value)

        if gap_differnece > 44:  # 16 + 16 + 8 or 2.5 characters
            print("gap: ", detected_boundary_gaps[index], detected_boundary_gaps[index + 1], "  gap difference: ",
                  gap_differnece)
            value1 = detected_boundary_gaps[index] + (gap_differnece // 3)
            value2 = detected_boundary_gaps[index + 1] - (gap_differnece // 3)

            if value1 in detected_content_columns:
                # then this is too long a letter and remove the value
                print("removing: ", value1)
                detected_content_columns.remove(value1)

            if value2 in detected_content_columns:
                # then this is too long a letter and remove the value
                print("removing: ", value2)
                detected_content_columns.remove(value2)

    detected_content_columns.sort()
    return detected_content_columns

